contracts/MockUSDC.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title MockUSDC
 * @dev A mock USDC token for testing purposes
 */
contract MockUSDC is ERC20, Ownable {
    uint8 private _decimals;

    constructor() ERC20("Mock USD Coin", "USDC") Ownable(msg.sender) {
        _decimals = 6; // USDC has 6 decimals
        _mint(msg.sender, 1000000 * 10**_decimals); // Mint 1M USDC to deployer
    }

    function decimals() public view virtual override returns (uint8) {
        return _decimals;
    }

    /**
     * @dev Mints tokens to a specified address (for testing)
     * @param to The address to mint tokens to
     * @param amount The amount to mint
     */
    function mint(address to, uint256 amount) external onlyOwner {
        _mint(to, amount);
    }

    /**
     * @dev Allows anyone to mint tokens for testing purposes
     * @param amount The amount to mint to the caller
     */
    function faucet(uint256 amount) external {
        require(amount <= 10000 * 10**_decimals, "Faucet limit exceeded");
        _mint(msg.sender, amount);
    }
}

contracts/Organization.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title Organization
 * @dev Individual organization contract for managing employees and payments
 */
contract Organization is Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    struct Employee {
        address employeeAddress;
        uint256 salaryPerSecond;
        uint256 startDate;
        uint256 lastPaymentDate;
        bool isActive;
        string name; // Optional employee name/identifier
    }

    struct PaymentSchedule {
        uint256 amount;
        uint256 nextPaymentDate;
        uint256 frequency; // in seconds (e.g., 2592000 for monthly)
        bool isActive;
    }

    // Organization details
    string public organizationName;
    IERC20 public paymentToken;

    // Employee management
    mapping(address => Employee) public employees;
    mapping(address => PaymentSchedule) public paymentSchedules;
    address[] public employeeList;

    // Payment tracking
    mapping(address => uint256) public totalPaid;
    mapping(address => uint256) public availableBalance;

    uint256 public totalFunds;
    uint256 public totalEmployees;

    // Events
    event EmployeeAdded(
        address indexed employee,
        uint256 salaryPerSecond,
        string name
    );

    event EmployeeRemoved(
        address indexed employee
    );

    event SalaryUpdated(
        address indexed employee,
        uint256 oldSalary,
        uint256 newSalary
    );

    event PaymentScheduleSet(
        address indexed employee,
        uint256 amount,
        uint256 frequency,
        uint256 nextPaymentDate
    );

    event FundsDeposited(
        address indexed depositor,
        uint256 amount
    );

    event PaymentProcessed(
        address indexed employee,
        uint256 amount
    );

    event PaymentWithdrawn(
        address indexed employee,
        uint256 amount
    );

    modifier onlyActiveEmployee(address employee) {
        require(employees[employee].isActive, "Employee not active");
        _;
    }

    constructor(
        string memory _organizationName,
        address _owner,
        address _paymentToken
    ) Ownable(_owner) {
        organizationName = _organizationName;
        paymentToken = IERC20(_paymentToken);
    }

    /**
     * @dev Add a new employee to the organization
     * @param employeeAddress The employee's wallet address
     * @param salaryPerSecond The salary amount per second
     * @param name Optional name/identifier for the employee
     */
    function addEmployee(
        address employeeAddress,
        uint256 salaryPerSecond,
        string memory name
    ) external onlyOwner {
        require(employeeAddress != address(0), "Invalid employee address");
        require(employeeAddress != owner(), "Cannot add owner as employee");
        require(!employees[employeeAddress].isActive, "Employee already exists");
        require(salaryPerSecond > 0, "Salary must be greater than 0");

        employees[employeeAddress] = Employee({
            employeeAddress: employeeAddress,
            salaryPerSecond: salaryPerSecond,
            startDate: block.timestamp,
            lastPaymentDate: block.timestamp,
            isActive: true,
            name: name
        });

        employeeList.push(employeeAddress);
        totalEmployees++;

        emit EmployeeAdded(employeeAddress, salaryPerSecond, name);
    }

    /**
     * @dev Remove an employee from the organization
     * @param employeeAddress The employee's wallet address
     */
    function removeEmployee(address employeeAddress) external onlyOwner onlyActiveEmployee(employeeAddress) {
        // Process any pending payments before removal
        _processPayment(employeeAddress);

        employees[employeeAddress].isActive = false;
        paymentSchedules[employeeAddress].isActive = false;
        totalEmployees--;

        // Remove from employee list
        for (uint i = 0; i < employeeList.length; i++) {
            if (employeeList[i] == employeeAddress) {
                employeeList[i] = employeeList[employeeList.length - 1];
                employeeList.pop();
                break;
            }
        }

        emit EmployeeRemoved(employeeAddress);
    }

    /**
     * @dev Update an employee's salary
     * @param employeeAddress The employee's wallet address
     * @param newSalaryPerSecond The new salary amount per second
     */
    function updateSalary(
        address employeeAddress,
        uint256 newSalaryPerSecond
    ) external onlyOwner onlyActiveEmployee(employeeAddress) {
        require(newSalaryPerSecond > 0, "Salary must be greater than 0");

        // Process payment with old salary first
        _processPayment(employeeAddress);

        uint256 oldSalary = employees[employeeAddress].salaryPerSecond;
        employees[employeeAddress].salaryPerSecond = newSalaryPerSecond;

        emit SalaryUpdated(employeeAddress, oldSalary, newSalaryPerSecond);
    }

    /**
     * @dev Set payment schedule for an employee
     * @param employeeAddress The employee's wallet address
     * @param amount The payment amount
     * @param frequency Payment frequency in seconds
     */
    function setPaymentSchedule(
        address employeeAddress,
        uint256 amount,
        uint256 frequency
    ) external onlyOwner onlyActiveEmployee(employeeAddress) {
        require(amount > 0, "Amount must be greater than 0");
        require(frequency > 0, "Frequency must be greater than 0");

        paymentSchedules[employeeAddress] = PaymentSchedule({
            amount: amount,
            nextPaymentDate: block.timestamp + frequency,
            frequency: frequency,
            isActive: true
        });

        emit PaymentScheduleSet(employeeAddress, amount, frequency, block.timestamp + frequency);
    }

    /**
     * @dev Deposit funds to the organization
     * @param amount The amount to deposit
     */
    function depositFunds(uint256 amount) external {
        require(amount > 0, "Amount must be greater than 0");

        paymentToken.safeTransferFrom(msg.sender, address(this), amount);
        totalFunds += amount;

        emit FundsDeposited(msg.sender, amount);
    }

    /**
     * @dev Process payment for an employee (streaming)
     * @param employeeAddress The employee's wallet address
     */
    function processPayment(address employeeAddress) external onlyActiveEmployee(employeeAddress) {
        _processPayment(employeeAddress);
    }

    /**
     * @dev Process payments for all employees
     */
    function processAllPayments() external onlyOwner {
        for (uint i = 0; i < employeeList.length; i++) {
            if (employees[employeeList[i]].isActive) {
                _processPayment(employeeList[i]);
            }
        }
    }

    /**
     * @dev Internal function to process payment for an employee
     * @param employeeAddress The employee's wallet address
     */
    function _processPayment(address employeeAddress) internal {
        Employee storage employee = employees[employeeAddress];
        if (!employee.isActive) return;

        uint256 timeElapsed = block.timestamp - employee.lastPaymentDate;
        uint256 payment = timeElapsed * employee.salaryPerSecond;

        if (payment > 0 && payment <= totalFunds) {
            availableBalance[employeeAddress] += payment;
            totalPaid[employeeAddress] += payment;
            totalFunds -= payment;
            employee.lastPaymentDate = block.timestamp;

            emit PaymentProcessed(employeeAddress, payment);
        }
    }

    /**
     * @dev Allow employee to withdraw their available balance
     */
    function withdrawPayment() external nonReentrant {
        require(employees[msg.sender].isActive, "Not an active employee");

        // Process any pending payments first
        _processPayment(msg.sender);

        uint256 amount = availableBalance[msg.sender];
        require(amount > 0, "No funds available");

        availableBalance[msg.sender] = 0;
        paymentToken.safeTransfer(msg.sender, amount);

        emit PaymentWithdrawn(msg.sender, amount);
    }

    /**
     * @dev Get employee details
     * @param employeeAddress The employee's wallet address
     */
    function getEmployee(address employeeAddress) external view returns (
        address,
        uint256,
        uint256,
        uint256,
        bool,
        string memory
    ) {
        Employee memory employee = employees[employeeAddress];
        return (
            employee.employeeAddress,
            employee.salaryPerSecond,
            employee.startDate,
            employee.lastPaymentDate,
            employee.isActive,
            employee.name
        );
    }

    /**
     * @dev Get payment schedule for an employee
     * @param employeeAddress The employee's wallet address
     */
    function getPaymentSchedule(address employeeAddress) external view returns (
        uint256,
        uint256,
        uint256,
        bool
    ) {
        PaymentSchedule memory schedule = paymentSchedules[employeeAddress];
        return (
            schedule.amount,
            schedule.nextPaymentDate,
            schedule.frequency,
            schedule.isActive
        );
    }

    /**
     * @dev Get all employees
     */
    function getAllEmployees() external view returns (address[] memory) {
        return employeeList;
    }

    /**
     * @dev Calculate pending payment for an employee
     * @param employeeAddress The employee's wallet address
     */
    function getPendingPayment(address employeeAddress) external view returns (uint256) {
        Employee memory employee = employees[employeeAddress];
        if (!employee.isActive) return 0;

        uint256 timeElapsed = block.timestamp - employee.lastPaymentDate;
        return timeElapsed * employee.salaryPerSecond;
    }

    /**
     * @dev Get organization stats
     */
    function getOrganizationStats() external view returns (
        uint256 _totalEmployees,
        uint256 _totalFunds,
        uint256 _totalPaidOut
    ) {
        uint256 totalPaidOut = 0;
        for (uint i = 0; i < employeeList.length; i++) {
            totalPaidOut += totalPaid[employeeList[i]];
        }

        return (totalEmployees, totalFunds, totalPaidOut);
    }

    /**
     * @dev Emergency withdraw (only owner)
     */
    function emergencyWithdraw() external onlyOwner {
        uint256 balance = paymentToken.balanceOf(address(this));
        paymentToken.safeTransfer(owner(), balance);
        totalFunds = 0;
    }
}

contracts/OrganizationFactory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "./Organization.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title OrganizationFactory
 * @dev Factory contract for creating and managing Organization contracts
 */
contract OrganizationFactory is Ownable {

    struct OrganizationInfo {
        address organizationContract;
        string name;
        address owner;
        uint256 createdAt;
        bool isActive;
    }

    // Default payment token (USDC)
    address public defaultPaymentToken;

    // Mapping from owner address to their organization
    mapping(address => address) public ownerToOrganization;

    // Mapping from organization contract to info
    mapping(address => OrganizationInfo) public organizationInfo;

    // Array of all organizations
    address[] public allOrganizations;

    // Total number of organizations
    uint256 public totalOrganizations;

    // Events
    event OrganizationCreated(
        address indexed owner,
        address indexed organizationContract,
        string name
    );

    event OrganizationDeactivated(
        address indexed organizationContract,
        address indexed owner
    );

    event DefaultPaymentTokenUpdated(
        address indexed oldToken,
        address indexed newToken
    );

    constructor(address _defaultPaymentToken) Ownable(msg.sender) {
        require(_defaultPaymentToken != address(0), "Invalid payment token");
        defaultPaymentToken = _defaultPaymentToken;
    }

    /**
     * @dev Create a new organization
     * @param organizationName The name of the organization
     * @param paymentToken The payment token address (use address(0) for default)
     */
    function createOrganization(
        string memory organizationName,
        address paymentToken
    ) external returns (address) {
        require(bytes(organizationName).length > 0, "Organization name required");
        require(ownerToOrganization[msg.sender] == address(0), "Organization already exists");

        // Use default payment token if not specified
        address tokenToUse = paymentToken == address(0) ? defaultPaymentToken : paymentToken;
        require(tokenToUse != address(0), "Invalid payment token");

        // Deploy new Organization contract
        Organization newOrg = new Organization(
            organizationName,
            msg.sender,
            tokenToUse
        );

        address orgAddress = address(newOrg);

        // Store organization info
        organizationInfo[orgAddress] = OrganizationInfo({
            organizationContract: orgAddress,
            name: organizationName,
            owner: msg.sender,
            createdAt: block.timestamp,
            isActive: true
        });

        // Update mappings
        ownerToOrganization[msg.sender] = orgAddress;
        allOrganizations.push(orgAddress);
        totalOrganizations++;

        emit OrganizationCreated(msg.sender, orgAddress, organizationName);

        return orgAddress;
    }

    /**
     * @dev Deactivate an organization (only owner or factory owner)
     * @param organizationContract The organization contract address
     */
    function deactivateOrganization(address organizationContract) external {
        OrganizationInfo storage info = organizationInfo[organizationContract];
        require(info.isActive, "Organization not active");
        require(
            msg.sender == info.owner || msg.sender == owner(),
            "Not authorized"
        );

        info.isActive = false;
        delete ownerToOrganization[info.owner];

        emit OrganizationDeactivated(organizationContract, info.owner);
    }

    /**
     * @dev Get organization contract for an owner
     * @param owner The owner address
     */
    function getOrganizationByOwner(address owner) external view returns (address) {
        return ownerToOrganization[owner];
    }

    /**
     * @dev Get organization info
     * @param organizationContract The organization contract address
     */
    function getOrganizationInfo(address organizationContract) external view returns (
        address,
        string memory,
        address,
        uint256,
        bool
    ) {
        OrganizationInfo memory info = organizationInfo[organizationContract];
        return (
            info.organizationContract,
            info.name,
            info.owner,
            info.createdAt,
            info.isActive
        );
    }

    /**
     * @dev Get all organizations (paginated)
     * @param offset Starting index
     * @param limit Number of organizations to return
     */
    function getOrganizations(uint256 offset, uint256 limit) external view returns (
        address[] memory organizations,
        string[] memory names,
        address[] memory owners,
        uint256[] memory createdAts,
        bool[] memory activeStatuses
    ) {
        require(offset < allOrganizations.length, "Offset out of bounds");

        uint256 end = offset + limit;
        if (end > allOrganizations.length) {
            end = allOrganizations.length;
        }

        uint256 length = end - offset;
        organizations = new address[](length);
        names = new string[](length);
        owners = new address[](length);
        createdAts = new uint256[](length);
        activeStatuses = new bool[](length);

        for (uint256 i = 0; i < length; i++) {
            address orgAddress = allOrganizations[offset + i];
            OrganizationInfo memory info = organizationInfo[orgAddress];

            organizations[i] = info.organizationContract;
            names[i] = info.name;
            owners[i] = info.owner;
            createdAts[i] = info.createdAt;
            activeStatuses[i] = info.isActive;
        }
    }

    /**
     * @dev Check if an address is a valid organization contract
     * @param organizationContract The contract address to check
     */
    function isValidOrganization(address organizationContract) external view returns (bool) {
        return organizationInfo[organizationContract].isActive;
    }

    /**
     * @dev Update default payment token (only factory owner)
     * @param newPaymentToken The new default payment token address
     */
    function updateDefaultPaymentToken(address newPaymentToken) external onlyOwner {
        require(newPaymentToken != address(0), "Invalid payment token");

        address oldToken = defaultPaymentToken;
        defaultPaymentToken = newPaymentToken;

        emit DefaultPaymentTokenUpdated(oldToken, newPaymentToken);
    }

    /**
     * @dev Get total number of active organizations
     */
    function getActiveOrganizationCount() external view returns (uint256) {
        uint256 activeCount = 0;
        for (uint256 i = 0; i < allOrganizations.length; i++) {
            if (organizationInfo[allOrganizations[i]].isActive) {
                activeCount++;
            }
        }
        return activeCount;
    }

    /**
     * @dev Get all organizations for a specific owner (in case of multiple)
     * @param owner The owner address
     */
    function getOrganizationsByOwner(address owner) external view returns (address[] memory) {
        address[] memory userOrgs = new address[](allOrganizations.length);
        uint256 count = 0;

        for (uint256 i = 0; i < allOrganizations.length; i++) {
            if (organizationInfo[allOrganizations[i]].owner == owner && 
                organizationInfo[allOrganizations[i]].isActive) {
                userOrgs[count] = allOrganizations[i];
                count++;
            }
        }

        // Resize array to actual count
        address[] memory result = new address[](count);
        for (uint256 i = 0; i < count; i++) {
            result[i] = userOrgs[i];
        }

        return result;
    }
}

